css:
@media (min-width:993px){
  .page--home .custom-html:nth-child(4) {
      background: url(/img/uploads/bg-new-blow.jpg) no-repeat top;
  }
.page--home .custom-html:nth-child(5) {
    background: url(/img/uploads/neww-blo-box.jpg) no-repeat top;
}

#influencers {
    background: url(/img/uploads/influ-new.jpg) no-repeat left top #c1eaf2;
}


.box-bg-gummie {
    padding-left: 18%;
        padding-top: 75px;
    max-width: 1056px;
}

.box-bg-gummie .title-blow {
    background: url(/img/uploads/blow-gummies.png) no-repeat 0;
    width: 240px;
    height: 108px;
}

.box-bg-gummie .oque-sao {
    display: flex;
    flex-direction: row;
    margin-right: 0;
      margin-bottom: 25px;
}

.box-bg-gummie .oque-sao h2 {
    font-size: 23px;
    margin: 15px;
    max-width: 520px;
    font-family: GoldplayAltBold;
}

.text-gummie {
    display: flex;
    flex-direction: column;
}

.box1__sobre {
    color: #303131;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    padding-right: 60px;
}

.box2__sobre {
    color: #f3237f;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    padding-right: 60px;
    line-height: 27px;
}

  #cta {
    background:#ed2a56;
    color:#fff;
    padding: 11px;
    max-width: 154px;
    border-radius: 8px;
    margin: 15px 0;
}

}


@media (min-width: 0px) and (max-width: 767px) {

  .box-bg-gummie .oque-sao h2 {
    font-size: 24px;
    margin: 15px;
    max-width: 520px;
    font-family: GoldplayAltBold;
    text-align: left;
}

.box-bg-gummie {
    padding-left: 0;
    padding-top: 350px;
    text-align: left;
}

.box1__sobre {
    color: #303131;
    font-size: 18px;
    font-family: 'GoldplayMedium';
}

.text-gummie {
  display:flex;
  flex-direction: column;
}

.text-sobre, .text-gummie {
    padding: 0px 15px;
}

.box2__sobre {
    color: #f3237f;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    line-height: 27px;
    margin: 15px 0;
}

#cta {
    background: #ed2a56;
    color: #fff;
    padding: 11px;
    max-width: 100%;
    border-radius: 8px;
    margin: 15px 0;
    text-align: center;
}

.text-sobre {
    flex-direction: column;
}

.page--home .custom-html:nth-child(5) {
  background: url(/img/uploads/blow-gumm-new.jpg) no-repeat top;
}

.page--home .custom-html:nth-child(4) {
    background: url(/img/uploads/neww-bg-mobile-box.jpg) no-repeat top #a9f5ff;
}

#influencers {
    background: url(/img/uploads/influ-new.jpg) no-repeat top #c1eaf2;
}

}
